coachView.loadJsFunction:
// console.log('Load Event Handler for Comments');
var _context = this.context;

function loadComments() {
    var processInstanceId = _context.options.processInstanceId.get("value");

    _context.options.getCommentsForInstance({
        params: {
            processInstanceId: processInstanceId
        },
        load: function(outputs) {
            var comments = outputs.comments;
            _context.binding.set("value", comments);
        },
        error: function(error) {
            console.error("Error in getCommentsForInstance()", error);
        }
    });
}

try {
    loadComments();
} catch (error) {
    console.error("Error in Load Event Handler for Comments Coach View", error);
}

$('button', this.context.element).click(function() {
    var processInstanceId = _context.options.processInstanceId.get("value");
    var newComment = $('textarea', _context.element)[0].value;

    if (newComment != undefined && newComment.length > 0) {

        _context.options.addCommentToInstance({
            params: {
                processInstanceId: processInstanceId,
                comment: newComment
            },
            load: function(outputs) {
                console.log("Comment Added");
                $('textarea', _context.element)[0].value = "";
                loadComments();
            },
            error: function(error) {
                console.log("Error in addCommentToInstance()", error)
            }
        })
    }
});
